// questo codice viene eseguito al momento del require
console.log("codice scritto direttamente");

// esposizione di una funzione in forma anonima:
// va fatto prima delle funzioni esplicite e si pu√≤ fare una sola funzione anonima
// altrimenti l'ultima funzione anonima sovrascrive quelle precedenti
function _anonima(){
    console.log("funzione anonima");
}
module.exports = _anonima;

// esposizione di funzioni in forma esplicita:
function _somma(a, b)
{
    return a + b;
}

function _moltiplicazione(a, b){  
    return a * b;  
}
module.exports.somma = _somma;
module.exports.moltiplicazione = _moltiplicazione;

// esposizione di un json:
let _json = {
    "nome":"pippo",
    "setNome":function(s) { this.nome = s }     
}
module.exports.json = _json;

// esposizione di una classe ES6:
class MyClass{
    constructor(){
        this.nome = "Pippo";
    }
    setNome(s){this.nome = s}
}
let _myClass = new MyClass();
module.exports.MyClass = _myClass;